sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }


    try {
        if (newValue == '1') {
            jslog("***************************************************************on change a été appelé !!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!");
            alert("This is a demo for debugging script!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!");
        }
    } catch (err) {
        jslog("LOG Message - nous avons une erreur!!!");
    }
}
